"use strict";
// FIXME:
// * support multiple backgrounds
// * also fix longhands

const parsers = require("cssstyle/lib/parsers");
const strings = require("cssstyle/lib/utils/strings");
const backgroundImage = require("cssstyle/lib/properties/backgroundImage");
const backgroundPosition = require("cssstyle/lib/properties/backgroundPosition");
const backgroundRepeat = require("cssstyle/lib/properties/backgroundRepeat");
const backgroundAttachment = require("cssstyle/lib/properties/backgroundAttachment");
const backgroundColor = require("cssstyle/lib/properties/backgroundColor");

const shorthandFor = new Map([
  ["background-image", backgroundImage],
  ["background-position", backgroundPosition],
  ["background-repeat", backgroundRepeat],
  ["background-attachment", backgroundAttachment],
  ["background-color", backgroundColor]
]);

module.exports.definition = {
  set(v) {
    v = parsers.prepareValue(v, this._global);
    if (/^none$/i.test(v)) {
      for (const [key] of shorthandFor) {
        this._setProperty(key, "");
      }
      this._setProperty("background", strings.asciiLowercase(v));
    } else if (parsers.hasVarFunc(v)) {
      for (const [key] of shorthandFor) {
        this._setProperty(key, "");
      }
      this._setProperty("background", v);
    } else {
      this._shorthandSetter("background", v, shorthandFor);
    }
  },
  get() {
    let val = this.getPropertyValue("background");
    if (parsers.hasVarFunc(val)) {
      return val;
    }
    val = this._shorthandGetter("background", shorthandFor);
    if (parsers.hasVarFunc(val)) {
      return "";
    }
    return val;
  },
  enumerable: true,
  configurable: true
};
